mapper.select:
select
  id, album_name, album_tag_id, album_description, user_id, collect_num, like_num
from spics.t_album


        and id = #{id}


        and album_name = #{albumName}


        and album_tag_id = #{albumTagId}


        and album_description = #{albumDescription}


        and user_id = #{userId}


        and collect_num = #{collectNum}


        and like_num = #{likeNum}
